mapper.select:
SELECT count(*)
FROM member
WHERE email = #{email}

SELECT *
FROM member
WHERE empno = #{empno}
AND password = #{password}

mapper.update:
UPDATE member
	SET name = #{name},
		auth = #{auth},
		status = #{status}
WHERE mid = #{mid}

UPDATE member 
	SET empno = #{empno},
		password = #{password}
WHERE mid = #{mid}

UPDATE member
	SET password = #{password}
WHERE empno = #{empno}
AND email = #{email}
