mapper.select:
select pid, pname, pcomment, pstart, pend
from project
where pid = #{pid}

mapper.update:
UPDATE project
   SET pname = #{pname, jdbcType=VARCHAR},
	   pcomment = #{pcomment, jdbcType=VARCHAR},
	   pstart = #{pstart, jdbcType=DATE},
	   pend = #{pend, jdbcType=DATE}		
WHERE pid = #{pid}
